import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function HeaderWithSidebar() {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [dropdownState, setDropdownState] = useState({
    resources: false,
    community: false,
    accessibility: false,
    techniques: false,
    game: false,
  });

  // Toggle dropdown visibility
  const toggleDropdown = (menu) => {
    setDropdownState((prevState) => ({
      ...prevState,
      [menu]: !prevState[menu],
    }));
  };

  // Toggle sidebar visibility
  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <>
      {/* Navbar */}
      <div className="navbar">
      <div className={`sidebar-container ${isSidebarOpen ? 'open' : 'closed'}`}>
      <button
        className="sidebar_button"
        onClick={toggleSidebar}
        aria-label={isSidebarOpen ? 'Close Sidebar' : 'Open Sidebar'}
      >
        ☰ {/* Hamburger Icon */}
      </button>
      <div className="sidebar_content">
        <ul className="sidebar_list">
          <li className="sidebar_item"><a href="#home">Home</a></li>
          <li className="sidebar_item"><a href="#about">About Us</a></li>
          <li className="sidebar_item"><a href="#contact">Contact Us</a></li>
          <li className="sidebar_item"><a href="#logout">Log Out</a></li>
          <li className="sidebar_item"><a href="#help">Help</a></li>
        </ul>
      </div>
    </div>
        <Link to="/">SooperStudies</Link>
        <div className="nav-center">
          {/* Resources Dropdown */}
          <div className="dropdown">
            <span className="headings resources" onClick={() => toggleDropdown('resources')}>
              Resources
            </span>
            {dropdownState.resources && (
              <div className="dropdown-content">
                <Link to="/ebooks">E-books</Link>
                <Link to="/pesu">PESU</Link>
                <Link to="/wikipedia">Wikipedia</Link>
                <Link to="/summaries">Summaries</Link>
                <Link to="/videos">Videos</Link>
              </div>
            )}
          </div>

          {/* Community Dropdown */}
          <div className="dropdown">
            <span className="headings community" onClick={() => toggleDropdown('community')}>
              Community
            </span>
            {dropdownState.community && (
              <div className="dropdown-content">
                <Link to="/forums">Forums</Link>
                <Link to="/events">Events</Link>
                <Link to="/discussions">Discussions</Link>
              </div>
            )}
          </div>

          {/* Accessibility Dropdown */}
          <div className="dropdown">
            <span className="headings accessibility" onClick={() => toggleDropdown('accessibility')}>
              Accessibility
            </span>
            {dropdownState.accessibility && (
              <div className="dropdown-content">
                <Link to="/text-to-speech">Text-to-Speech</Link>
                <Link to="/screen-readers">Screen Readers</Link>
                <Link to="/high-contrast">High Contrast</Link>
                <Link to="/bookmarks">Bookmarks</Link>
              </div>
            )}
          </div>

          {/* Techniques Dropdown */}
          <div className="dropdown">
            <span className="headings study" onClick={() => toggleDropdown('techniques')}>
              Techniques
            </span>
            {dropdownState.techniques && (
              <div className="dropdown-content">
                <Link to="/pomodoro">Pomodoro</Link>
                <Link to="/flashcards">Flashcards</Link>
                <Link to="/mind-maps">Mind Maps</Link>
              </div>
            )}
          </div>

          {/* Game Dropdown */}
          <div className="dropdown">
            <span className="headings game" onClick={() => toggleDropdown('game')}>
              Game
            </span>
            {dropdownState.game && (
              <div className="dropdown-content">
                <Link to="/quizzes">Quizzes</Link>
                <Link to="/memory-games">Memory Games</Link>
                <Link to="/puzzles">Puzzles</Link>
                <Link to="/achievements">Achievements</Link>
                <Link to="/rewards">Rewards</Link>
              </div>
            )}
          </div>
        </div>

        {/* Sidebar Toggle Button */}
        <button
          className="sidebar_button"
          onClick={toggleSidebar}
          aria-label="Toggle Sidebar"
        >
          ☰
        </button>

        {/* Search Input */}
        <div className="search">
          <input type="text" placeholder="Search..." />
        </div>
      </div>

      {/* Sidebar */}
      <div className={`sidebar-container ${isSidebarOpen ? 'open' : ''}`}>
        <div className="sidebar_content">
          <ul className="sidebar_list">
            <li className="sidebar_item"><Link to="/dashboard">Dashboard</Link></li>
            <li className="sidebar_item"><Link to="/tasks">Tasks</Link></li>
            <li className="sidebar_item"><Link to="/notifications">Notifications</Link></li>
            <li className="sidebar_item"><Link to="/settings">Settings</Link></li>
            <li className="sidebar_item"><Link to="/logout">Logout</Link></li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default HeaderWithSidebar;
