import React, { useState, useEffect, useRef } from 'react';

function HeaderWithSidebar() {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const sidebarRef = useRef(null);
  const buttonRef = useRef(null);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const handleClickOutside = (e) => {
    if (
      sidebarRef.current && !sidebarRef.current.contains(e.target) &&
      buttonRef.current && !buttonRef.current.contains(e.target)
    ) {
      setSidebarOpen(false); // Close sidebar if click is outside
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <>
      <div className="navbar">
        <button
          ref={buttonRef}
          className="sidebar_button"
          onClick={toggleSidebar}
          aria-label="Toggle Sidebar"
        >
          â˜°
        </button>
        <a href="#" className="sooperstudieslink">SooperStudies</a>
        <div className="nav-center">
          <div className="dropdown">
            <span className="headings">Resources</span>
            <div className="dropdown-content">
              <a href="#">E-books</a>
              <a href="#">PESU</a>
              <a href="#">Wikipedia</a>
              <a href="#">Summaries</a>
              <a href="#">Videos</a>
            </div>
          </div>
        </div>
        <div className="search">
          <input type="text" placeholder="Search..." />
        </div>
      </div>

      <div
        ref={sidebarRef}
        className={`sidebar-container ${isSidebarOpen ? 'open' : ''}`}
      >
        <div className="sidebar_content">
          <ul className="sidebar_list">
            <li className="sidebar_item"><a href="#">Dashboard</a></li>
            <li className="sidebar_item"><a href="#">Tasks</a></li>
            <li className="sidebar_item"><a href="#">Notifications</a></li>
            <li className="sidebar_item"><a href="#">Settings</a></li>
            <li className="sidebar_item"><a href="#">Logout</a></li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default HeaderWithSidebar;
