import React, { useEffect, useState } from 'react';
import HeaderWithSidebar from './Sidebar';
import axios from 'axios';
import './App.css';
import './Homepage.css';

function Homepage({ username }) {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks when the component mounts
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks/${username}`);
        setTasks(response.data.tasks);  // Update the state with the fetched tasks
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [username]);

  return (
    <div className="main">
      <HeaderWithSidebar />
      <div className="dashboard-container">
        {/* Welcome Message Section */}
        <div className="welcome-message">
          <h2>Welcome to SooperStudies!</h2>
          <p>We’re excited to have you here. Let’s make learning awesome!</p>
        </div>

        {/* Tasks To Do Section */}
        <div className="tasks-to-do">
          <h3>Tasks To Do</h3>
          <div className="tasks-grid">
            {tasks.length === 0 ? (
              <p>No tasks available. Start by adding some tasks!</p>
            ) : (
              tasks.map((task, index) => (
                <div key={index} className="task-item">
                  {task}
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
